import React, { useState } from 'react';
import PropTypes from 'prop-types';
import '@vkontakte/vkui/dist/vkui.css';
import { View, Input, Panel, PanelHeader, PanelHeaderBack, Button, FixedLayout, Text, ContentCard, Avatar, Spacing } from '@vkontakte/vkui';

import draw_db from './dbs/draw.json'

function Draw(props) {

    const count = 27

    const [number, setNumber] = useState(5)

    const [countTimes, setCountTimes] = useState(1)

    const getRandomNumber = (e) => {
        e.preventDefault()
        setNumber(Math.floor(Math.random() * (count + 1)))
        // console.log(number)
    }

    const getText = () => {
        let temp = countTimes
        for (let i of temp) {
            <Text weight='semibold'>{draw_db[number].word}</Text>
        }
    }

    var cardMap = Object.keys(draw_db).map((msg) =>
        <ContentCard
            size="l"
            header={"попробуй объединить этот список в один рисунок:"}
            // subtitle={books_db[number].from}
            // maxHeight={200}
            text={
                getText()
            }
        >
        </ContentCard>
    )

    return (
        <Panel id={props.id}>
            <Spacing size={120}></Spacing>
            <FixedLayout filled vertical='top'>
                <PanelHeader
                    left={<PanelHeaderBack onClick={props.go} data-to="home" />}
                >чё порисовать?
                </PanelHeader>
                <Spacing></Spacing>
                <Button type="submit" onClick={getRandomNumber} stretched size="l">скажи</Button>
                <Spacing></Spacing>

                <Input
                    type="number"
                    min={1}
                    max={20}
                    value={countTimes}
                    onChange={({ target: { value } }) => setCountTimes(Number(value))}
                />
                <Spacing></Spacing>
            </FixedLayout>
            <Spacing size={50}></Spacing>
            <Panel>
                {cardMap[0]}
            </Panel>
        </Panel>
    )
}

Draw.propTypes = {
    id: PropTypes.string.isRequired,
    go: PropTypes.func.isRequired,
};

export default Draw;
