import React from 'react';
import PropTypes from 'prop-types';
import '@vkontakte/vkui/dist/vkui.css';
import { Panel, PanelHeader, PanelHeaderBack, Button, FixedLayout, Text, ContentCard, Avatar, Spacing } from '@vkontakte/vkui';

import cinema_db from './dbs/cinema.json'
import { useState } from 'react/cjs/react.production.min';

// fetch(cinema_db).then(cinema_db => console.log(cinema_db))

function Cinema(props) {
	const [count, setCount] = useState(Array(cinema_db))

	const [rand, setRand] = useState(1)

	const getRandomInteger = (e) => {
		e.preventDefault();
		setRand(Math.floor(Math.random() * count))
		console.log(rand[0].value)
	}

	return (
		<Panel id={props.id}>
			<FixedLayout>
				<PanelHeader
					left={<PanelHeaderBack onClick={props.go} data-to="home" />}
				>чё посмотреть?
				</PanelHeader>
				<Button type="submit" onClick={getRandomInteger} stretched size="l">скажи</Button>
				<ContentCard
					size="l"
					header={<Avatar size={150} src={cinema_db[1].img}/>}	
					subtitle={cinema_db[1].from}
					maxHeight={200}
					text={<div>
						<Text weight='semibold'>{cinema_db[1].name}</Text>
						<Text>Режиссёр: {cinema_db[1].dir}</Text>
						<Text color='#ff0000'>КП: {cinema_db[1].kp} IMDb: {cinema_db[1].imdb}</Text>
						<Spacing/>
						<Text>{cinema_db[1].desc}</Text></div>
					}
				>	
				</ContentCard>
			</FixedLayout>
		</Panel>
	)
}

Cinema.propTypes = {
	id: PropTypes.string.isRequired,
	go: PropTypes.func.isRequired,
};

export default Cinema;
